#include "common.h"class Solution {public:    int search(vector<int>& nums, int target) {        size_t p1 = 0, p2 = nums.size();        while(p1 - p2 > 1) {            size_t mid = (p1 + p2) / 2;            if (nums[mid] == target) return mid;            else {                if (nums[p1] < nums[mid]) {                    if (target >= nums[p1] && target <= nums[mid])                        p2 = mid;                    else p1 = mid + 1;                } else {                    if (target >= nums[mid] && target <= nums[p2 - 1])                        p1 = mid + 1;                    else p2 = mid;                }            }        }        return -1;    }};int main() {    Solution s;}